$(document).ready(function () {

    /** === Добавление товара в корзину === */
    // Ловим событие клика по кнопке добавить в корзину
    $(document).on("click", ".add-to-cart", function (e) {
        // Блокируем его базовое действие
        e.preventDefault();

        // Берем элемент счетчика в значке корзины и берем оттуда значение
        var goodsInCartCount = $("#in-cart-count");
        var cartCount = parseInt(goodsInCartCount.text() || 0);
        
        // Если в корзине более 30 товаров
        if (cartCount < 30){
            // Получаем id товара из атрибута data-product-id
            var product_id = $(this).data("product-id");
    
            // Из атрибута href берем ссылку на контроллер django
            var add_to_cart_url = $(this).attr("href");
            
            // делаем post запрос через ajax не перезагружая страницу
            $.ajax({
                type: "POST",
                url: add_to_cart_url,
                data: {
                    product_id: product_id,
                    csrfmiddlewaretoken: $("[name=csrfmiddlewaretoken]").val(),
                },
                success: function (data) {
                    // Сообщение
                    Swal.fire({
                        type: 'success',
                        title: "Успешно!",
                        text: "Товар успешно добавлен в корзину",
                        timer: 3000, // Время в миллисекундах (3 секунды)
                        confirmButtonText: "OK",
                        background: "#f2f2f2",
                        padding: "1rem",
                    });
    
                    // Увеличиваем количество товаров в корзине (отрисовка в шаблоне)
                    cartCount++;
                    goodsInCartCount.text(cartCount);
    
                    // Меняем содержимое корзины на ответ от django (новый отрисованный фрагмент разметки корзины)
                    var cartItemsContainer = $("#cart-items-container");
                    cartItemsContainer.html(data.cart_items_html);
    
                },
                error: function (data) {
                    console.log("Ошибка при добавлении товара в корзину");
                },
            });
        }
        else{
            Swal.fire({
                type: 'error',
                title: 'Oops...',
                text: 'Товаров не может быть больше 30',
                confirmButtonClass: 'btn btn-confirm mt-2',
                footer: 'Для дополнительной информации свяжитесь с менеджером'
            })
        }
    });

        /** === Удаление товара из корзины === */
    // Ловим событие клика по кнопке удалить товар из корзины
    $(document).on("click", ".cart-delete", function (e) {
        // Блокируем его базовое действие
        e.preventDefault();

        // Берем элемент счетчика в значке корзины и берем оттуда значение
        var goodsInCartCount = $("#in-cart-count");
        var cartCount = parseInt(goodsInCartCount.text() || 0);

        // Получаем id корзины из атрибута data-cart-id
        var cart_id = $(this).data("cart-id");
        // Из атрибута href берем ссылку на контроллер django
        var remove_from_cart = $(this).attr("href");

        // делаем post запрос через ajax не перезагружая страницу
        $.ajax({

            type: "POST",
            url: remove_from_cart,
            data: {
                cart_id: cart_id,
                csrfmiddlewaretoken: $("[name=csrfmiddlewaretoken]").val(),
            },
            success: function (data) {
                // Сообщение
                alertDelete()

                // Уменьшаем количество товаров в корзине (отрисовка)
                cartCount -= data.quantity_deleted;
                goodsInCartCount.text(cartCount);

                // Меняем содержимое корзины на ответ от django (новый отрисованный фрагмент разметки корзины)
                var cartItemsContainer = $("#cart-items-container");
                cartItemsContainer.html(data.cart_items_html);

            },
            error: function (data) {
                console.log("Ошибка при добавлении товара в корзину");
            },
        });
    });

    /** === Изменение кол-ва товаров в корзине +/- === */
    // Обработчик события для уменьшения значения
    $(document).on("click", ".decrement", function () {
        // Берем ссылку на контроллер django из атрибута data-cart-change-url
        var url = $(this).data("cart-change-url");
        // Берем id корзины из атрибута data-cart-id
        var cartID = $(this).data("cart-id");
        // Ищем ближайшеий input с количеством 
        var $input = $(this).closest('.align-items-center').find('.number');
        // Берем значение количества товара
        var currentValue = parseInt($input.val());

        // Если количества больше одного, то только тогда делаем -1
        if (currentValue > 1) {
            $input.val(currentValue - 1);
            // Запускаем функцию определенную ниже
            // с аргументами (id карты, новое количество, количество уменьшилось или прибавилось, url)
            updateCart(cartID, currentValue - 1, -1, url);
        }
    });

    // Обработчик события для увеличения значения
    $(document).on("click", ".increment", function () {
        // Берем ссылку на контроллер django из атрибута data-cart-change-url
        var url = $(this).data("cart-change-url");

        // Берем id корзины из атрибута data-cart-id
        var cartID = $(this).data("cart-id");

        // Ищем ближайшеий input с количеством 
        var $input = $(this).closest('.align-items-center').find('.number');

        // Берем значение количества товара
        var currentValue = parseInt($input.val());

        // Берем элемент счетчика в значке корзины и берем оттуда значение
        var goodsInCartCount = $("#in-cart-count");
        var cartCount = parseInt(goodsInCartCount.text() || 0);
        
        if (cartCount < 30){
            $input.val(currentValue + 1);

            // Запускаем функцию определенную ниже
            // с аргументами (id карты, новое количество, количество уменьшилось или прибавилось, url)
            updateCart(cartID, currentValue + 1, 1, url);
        }
        else{
            Swal.fire({
                type: 'error',
                title: 'Oops...',
                text: 'Товаров не может быть больше 30',
                confirmButtonClass: 'btn btn-confirm mt-2',
                footer: 'Для дополнительной информации свяжитесь с менеджером'
            })
        }    
    });

    function updateCart(cartID, quantity, change, url) {
        $.ajax({
            type: "POST",
            url: url,
            data: {
                cart_id: cartID,
                quantity: quantity,
                csrfmiddlewaretoken: $("[name=csrfmiddlewaretoken]").val(),
            },

            success: function (data) {
                
                // Изменяем количество товаров в корзине
                var goodsInCartCount = $("#in-cart-count");
                var cartCount = parseInt(goodsInCartCount.text() || 0);
                cartCount += change;
                goodsInCartCount.text(cartCount);

                // Меняем содержимое корзины
                var cartItemsContainer = $("#cart-items-container");
                cartItemsContainer.html(data.cart_items_html);

            },
            error: function (data) {
                console.log("Ошибка при добавлении товара в корзину");
            },
        });
    }

    // Оповещение об удалении из корзины
    function alertDelete(){     
        const alertBox = $("#customAlert");
        // Показываем алерт с эффектом появления
        alertBox.removeClass("d-none").hide().fadeIn(500);

        // Через 3 секунды скрываем алерт с эффектом исчезновения
        setTimeout(function () {
            alertBox.fadeOut(500, function () {
                alertBox.addClass("d-none");
            });
        }, 3000);
    }
});



    


